catalog_script_client.script:
function onLoad() {

    var call = new GlideAjax('check_record_name_card');
    call.addParam('sysparm_name', 'record');
    call.getXML(getResponse);

    function getResponse(response) {
        var result = response.responseXML.documentElement.getAttribute('answer');
        if (result > 35 ) {
            var fields = g_form.getEditableFields();
            for (var x = 0; x < fields.length; x++) {
                g_form.setMandatory(fields[x], false);
                g_form.setReadOnly(fields[x], true);

            }
			alert("ไม่สามารถสมัครได้ เนื่องจากสมาชิกเต็มแล้ว");
        }
    }

}
