catalog_script_client.script:
function onSubmit() {

    var con = g_form.getValue('u_confirm');

    if (!g_form.ajaxComplete) {
        check();
        return false;
    }

    function check() {
        if (con == "false") {
			g_form.addErrorMessage("ไม่สามารถสมัครสมาชิกได้");
        }else{
			g_form.ajaxComplete = true;
			g_form.submit();
		}
    }

}
