catalog_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below

    var call = new GlideAjax('LoadDetail');
    call.addParam('sysparm_name', 'load');
    call.getXML(getResponse);

    function getResponse(response) {
        var answer = response.responseXML.documentElement.getAttribute('answer');
        if (answer == "{}") {
            g_form.setMandatory('u_confirm', false);
            g_form.setValue('u_confirm', false);
            g_form.setReadOnly('u_confirm', true);
        } else {
            var result = JSON.parse(answer);

            //read only
            g_form.setReadOnly('u_fname', true);
            g_form.setReadOnly('u_lname', true);
            g_form.setReadOnly('u_phone', true);
            g_form.setReadOnly('u_email', true);
            g_form.setReadOnly('u_id_card', true);

            //input info to fields
            g_form.setValue('u_fname', result.fname);
            g_form.setValue('u_lname', result.lname);
            g_form.setValue('u_phone', result.phone);
            g_form.setValue('u_email', result.email);
            g_form.setValue('u_id_card', result.id_card);
        }
    }
}
