catalog_script_client.script:
function onSubmit() {
    //Type appropriate comment here, and begin script below

    var id_card = g_form.getValue('u_id_card');
    var num_car = g_form.getValue('u_number_car');
    var location = g_form.getValue('u_location');
    var start = g_form.getValue('u_start_dt');
    var end = g_form.getValue('u_end_dt');
    var con = g_form.getValue('u_confirm');
    var date_all = g_form.getValue('u_date_all_in');

    if (con == "false") {
        g_form.addErrorMessage("ไม่สามารถจองได้ เนื่องจากคุณไม่มีสมาชิก");
        return false;
    } else if (!g_form.ajaxComplete) {
        check();
        return false;
    }


    function check() {
        var call = new GlideAjax('Check_location');
        call.addParam('sysparm_name', 'query_location');
        call.addParam('id', id_card);
        call.addParam('location', location);
        call.addParam('st', start);
        call.addParam('end', end);
        call.addParam('date_all', date_all);


        call.getXML(getResponse);

        function getResponse(response) {
            var result = response.responseXML.documentElement.getAttribute('answer');
            if (result != null) {
                if (start == "" || end == "") {
                    g_form.addErrorMessage("คุณได้ทำการจองไปแล้ว");
                    g_form.clearValue('u_date_all_in');
                    g_form.clearValue('u_hours');
                } else {
                    g_form.addErrorMessage("คุณได้ทำการจองไปแล้ว");
                    g_form.clearValue('u_date_all_in');
                    g_form.clearValue('u_hours');
                    g_form.clearValue('u_price');
                }
            } else {
                //                 g_form.ajaxComplete = true;
                full();
                //                 g_form.submit();
            }
        }
    }

    function full() {
        var check = new GlideAjax('Check_location');
        check.addParam('sysparm_name', 'location_full');
        check.addParam('id', id_card);
        //         check.addParam('num_car', num_car);
        check.addParam('location', location);
        check.addParam('st', start);
        check.addParam('end', end);
        check.addParam('date_all', date_all);


        check.getXML(getResponse);

        function getResponse(response) {
            var result = response.responseXML.documentElement.getAttribute('answer');
            if (result >= 10) {
                if (start == "" || end == "") {
                    g_form.addErrorMessage("ที่จอดรถชั้นที่ "+location+" ณ เวลาที่คุณกรอก เต็มแล้ว");
                    g_form.clearValue('u_date_all_in');
                    g_form.clearValue('u_hours');
                } else {
                    g_form.addErrorMessage("ที่จอดรถชั้นที่ "+location+" ณ เวลาที่คุณกรอก เต็มแล้ว");
                    g_form.clearValue('u_date_all_in');
                    g_form.clearValue('u_hours');
                    g_form.clearValue('u_price');
                }
            } else {
                g_form.ajaxComplete = true;
                g_form.submit();
            }
        }
    }

}
