sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

	data.record = [];
		var table = new GlideRecord('x_950909_car_park_name_card');
		//table.addQuery('user', gs.getUser().getID());
		table.addEncodedQuery("	sys_created_by=javascript:gs.getUserName()^u_state=1");
		//table.addQuery('u_state_approve',"1");
		//table.orderByDesc('sys_created_on');
		//table.setLimit(options.maximum_records_to_display);

		table.query();
		while (table.next()) {
			var RecordObj = {};
			//use service portal helper method to get some display values
			$sp.getRecordDisplayValues(RecordObj, table, 'number,u_fname,u_lname,sys_id,u_phone,u_email,u_sub_phone,u_subemail,u_id_card,u_number_home,u_moo,u_village,u_road,u_alley,u_district,u_subdistrict,u_province,u_zipcode,u_state_approve');
			//get the first 20 characters of the description
			//noteObj.note = noteGR.getValue('u_fname').slice(0,20);
			//push the populated obj into the array
			data.record.push(RecordObj);	
		}

})();
