sp_widget.client_script:
api.controller=function($scope) {
  /* widget controller */
  var c = this;

	$scope.Delete = function(info){
		$scope.data.number = info.number;
		alert(info.number);
		//$scope.server.update();
		alert("สวัสดีครับ");
		g_form.setValue('u_cancel',true);
	}

};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.array=[];
	var sys = $sp.getParameter("sys_id");

	var table = new GlideRecord('x_950909_car_park_detail');
	table.addQuery('sys_id',sys);
	table.query();
	if(table.next()){

		var info={};
		info.number = table.getValue('number');
		info.fname = table.getValue('u_fname');

		data.array.push(info);
	}

})();
