var Check_location = Class.create();
Check_location.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    query_location: function() {
        var id = this.getParameter('id');
        var location = this.getParameter('location');
        var st = new GlideDateTime(this.getParameter('st'));
        var ed = new GlideDateTime(this.getParameter('end'));
        //date all
        var dt_all = new GlideDateTime(this.getParameter('date_all'));

        if (st != "" || ed != "") {
            var d_all = ed.getDate().getByFormat('yyyy-MM-dd');

            //format
            var Date_st = st.getDate().getByFormat("yyyy-MM-dd");
            var Time_st = st.getTime().getByFormat("HH:mm:ss");
            var Date_ed = ed.getDate().getByFormat("yyyy-MM-dd");
            var Time_ed = ed.getTime().getByFormat("HH:mm:ss");

            //Query
            var table = new GlideRecord('x_950909_car_park_car_parking');
table.addEncodedQuery("u_start_dt<=javascript:gs.dateGenerate('"+Date_ed+"','"+Time_ed+"')^u_end_dt>=javascript:gs.dateGenerate('"+Date_st+"','"+Time_st+"')^u_id_card="+id+"^u_state_approve=1^NQu_date_all_inON"+Date_st+"@javascript:gs.dateGenerate('"+Date_st+"','start')@javascript:gs.dateGenerate('"+Date_st+"','end')^u_id_card="+id+"^u_state_approve=1");
            table.query();
            while (table.next()) {
                return table.getRowCount();
            }

        } else {

            var d_all1 = dt_all.getDate().getByFormat('yyyy-MM-dd');
            var table1 = new GlideRecord('x_950909_car_park_car_parking');
            table1.addEncodedQuery("u_date_all_inON" + d_all1 + "@javascript:gs.dateGenerate('" + d_all1 + "','start')@javascript:gs.dateGenerate('" + d_all1 + "','end')^u_id_card=" + id + "^u_state_approve=1^NQu_start_dt<=javascript:gs.dateGenerate('" + d_all1 + "','23:59:59')^u_end_dt>=javascript:gs.dateGenerate('" + d_all1 + "','00:00:00')^u_id_card=" + id + "^u_state_approve=1");
            table1.query();
            while (table1.next()) {
                return table1.getRowCount();
            }
        }
    },



    //function query หาที่จอด เต็มหรือไม่ ในเวลาที่กรอกมา
    location_full: function() {
        var id1 = this.getParameter('id');
        //         var num_car1 = this.getParameter('num_car');
        var location1 = this.getParameter('location');
        var st1 = new GlideDateTime(this.getParameter('st'));
        var ed1 = new GlideDateTime(this.getParameter('end'));
        //         //date all
        var dt_all1 = new GlideDateTime(this.getParameter('date_all'));
        //         //format
        var Date_st1 = st1.getDate().getByFormat("yyyy-MM-dd");
        var Time_st1 = st1.getTime().getByFormat("HH:mm:ss");
        var Date_ed1 = ed1.getDate().getByFormat("yyyy-MM-dd");
        var Time_ed1 = ed1.getTime().getByFormat("HH:mm:ss");

        //         var date = dt_all1.getDate().getByFormat('yyyy-MM-dd');

        var table2 = new GlideRecord('x_950909_car_park_car_parking');

        table2.addEncodedQuery("u_start_dt<=javascript:gs.dateGenerate('" + Date_ed1 + "','" + Time_ed1 + "')^u_end_dt>=javascript:gs.dateGenerate('" + Date_st1 + "','" + Time_st1 + "')^u_state_approve=1^u_location=" + location1 + "^NQu_date_all_inON" + Date_st1 + "@javascript:gs.dateGenerate('" + Date_st1 + "','start')@javascript:gs.dateGenerate('" + Date_ed1 + "','end')^u_state_approve=1^u_location=" + location1 + "");

        table2.query();

        while (table2.next()) {
            return table2.getRowCount();
        }

    },

    type: 'Check_location'
});
