sys_script_include.script:
var Call_scheduled_export_to_server = Class.create();
Call_scheduled_export_to_server.prototype = {
	initialize: function() {
	},
	export: function() {
		var gr = new GlideRecord("scheduled_data_export");

		gr.addQuery("sys_id", 'e925e88a4761311021f49128436d434d'); // sys_id of your scheduled export
		gr.query();
		if (gr.next()) {
			gs.executeNow(gr); // if using in scoped app
			//SncTriggerSynchronizer.executeNow(gr); // if using in non-scope app i.e global
		}
	},
	type: 'Call_scheduled_export_to_server'
};
