sys_script_include.script:
var check_scan_qrcode  = Class.create();
check_scan_qrcode .prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	check: function(){
		var st = new GlideDateTime(this.getParameter('st_dt'));
		var ed = new GlideDateTime(this.getParameter('ed_dt'));
		var all = new GlideDateTime(this.getParameter('all_dt'));

		var time = new GlideDateTime();
		var hours = 7*60*60;
		time.addSeconds(hours);
		var time_day = time.getDate().getByFormat('dd');
		var time_time = time.getTime().getByFormat('HH:mm');

		var open = new GlideTime();
		open.setValue("10:00:00");
		var open_time = open.getByFormat('HH:mm');

		var close = new GlideTime();
		close.setValue("21:00:00");
		var close_time = close.getByFormat('HH:mm');		//var close = new GlideTime("21:00:00");


		if(all != ""){
			var all_day = all.getDate().getByFormat('dd');
			if(all_day == time_day && time_time >= open_time && time_time <= close_time){
				return "Daily-correct";
			}else{
				return "Error";
			}

		}else if(all == ""){
			var st_day = st.getDate().getByFormat('dd');
			var ed_day = ed.getDate().getByFormat('dd');
			var ed_time = ed.getTime().getByFormat('HH:mm');
			var st_time = st.getTime().getByFormat('HH:mm');

			if(st_day == time_day && time_time >= st_time && time_time <= ed_time){
				return "Hourly-correct";
			}else{
				return "Errorrr";
			}
		}
	},

	type: 'check_scan_qrcode '
});
