sys_script_include.script:
var LoadDetail = Class.create();
LoadDetail.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {


    load: function() {

        var table = new GlideRecord('x_950909_car_park_detail_card');
        table.addQuery('u_user_id', gs.getUserID());
		table.addQuery('u_state','1');
        table.query();

        var array = {};
        while (table.next()) {
            array.fname = table.u_fname.toString();
            array.lname = table.u_lname.toString();
            array.phone = table.u_phone.toString();
            array.email = table.u_email.toString();
            array.id_card = table.u_id_card.toString();
        }

		return JSON.stringify(array);
// 		if(array.length === 0){
// 			return "ERROR";
// 		}else{
// 			return JSON.stringify(array);
// 		}
    },

    type: 'LoadDetail'
});
