sys_ui_page.client_script:
var qr;
(function() {
    var abc = "GRAB THE VALUE FROM THR FORM"; //e.g:- g_form.getValue('number');
    qr = new QRious({
        element: document.getElementById('qr-code'),
        size: 200,
        value: abc
    });
})();

function generateQRCode() {
    qr.set({
        foreground: 'black',
        size: 200,
        value: abc
    });
}
